const incident = $('Webhook Execute Action').first().json;
const ragResults = $input.first().json;
incident.rag_history = [];
if (ragResults.result && ragResults.result.length > 0) {
  incident.rag_history = ragResults.result.map(r => ({
    score: r.score,
    service: r.payload?.service_name,
    solution: r.payload?.solution_applied,
    success: r.payload?.resolution_success
  }));
  incident.has_similar_incidents = true;
  const successfulFix = ragResults.result.find(r => r.payload?.resolution_success === true);
  if (successfulFix) {
    incident.suggested_from_history = successfulFix.payload?.solution_applied;
  }
} else {
  incident.has_similar_incidents = false;
}
return [{ json: incident }];

const incident = $input.first().json;
const command = incident.level_1_action;
const safePatterns = [
  /^systemctl (restart|start|stop|reload|status) [a-z0-9-]+$/,
  /^docker (restart|start|stop) [a-z0-9_-]+$/,
  /^docker logs --tail \d+ [a-z0-9_-]+$/,
  /^journalctl --vacuum-(size|time)=\S+$/,
  /^docker system prune -f$/
];
const blockedPatterns = [
  /rm\s+-rf/i,
  /rm\s+-r\s+\//i,
  /dd\s+if=/i,
  /mkfs/i,
  /chmod\s+777/i,
  /DROP\s+(DATABASE|TABLE)/i
];
const isBlocked = blockedPatterns.some(p => p.test(command));
if (isBlocked) {
  incident.validation = { is_valid: false, reason: 'Blocked pattern', command };
  return [{ json: incident }];
}
const isSafe = safePatterns.some(p => p.test(command));
incident.validation = { is_valid: isSafe, reason: isSafe ? 'Safe pattern' : 'Not in whitelist', command };
return [{ json: incident }];

const incident = $('Webhook Execute Action').first().json;
const executeResult = $('SSH - Executer Action').first().json;
const verifyResult = $('SSH - Verifier Service').first().json;
const httpCode = verifyResult.stdout?.trim();
const success = httpCode && httpCode.startsWith('2');
incident.level_1_success = success;
incident.resolved_at = success ? new Date().toISOString() : null;
incident.execution_result = { stdout: executeResult.stdout, stderr: executeResult.stderr, exit_code: executeResult.exitCode, http_code: httpCode };
return [{ json: incident }];

