const incident = $('Contexte RAG N2').first().json;
const claudeResponse = $input.first().json;
try {
  const content = claudeResponse.content?.[0]?.text || '';
  const jsonMatch = content.match(/\{[\s\S]*?\"root_cause\"[\s\S]*?\}/m);
  if (jsonMatch) {
    incident.level_2_recommendation = JSON.parse(jsonMatch[0]);
  } else { throw new Error('No JSON'); }
} catch (e) {
  incident.level_2_recommendation = { root_cause: 'Parse error', analysis: 'Manual review', severity: 'high', requires_human_approval: true };
}
return [{ json: incident }];

